import React from 'react';
import logo from './logo.svg';
import './App.css';
import GetOnlinePosts from './components/OnlinePosts/GetOnlinePosts';

let users = [
  {
    name: "Leonard Rogers",
    email: "egestas@justonecante.org"
  },
  {
    name: "Walker Pace",
    email: "erat.eget.tincidunt@idsapienCras.org"
